// nav icom
(function($){
	$('#nav-icon').click(function(){
		$(this).toggleClass('open');
		$(this).parent('.menu-icon').toggleClass('move');
	});
})(jQuery);

// parallax

(function($) {
	if ($("#parallax-window").length) {
		//parallax();
	}
})(jQuery);

// slick
(function($) {
	$(".slick").slick({
		infinite: false,
		slidesToShow: 1,
		slidesToScroll: 1,
	});
})(jQuery);

//scroll to div
(function($) {
  $('a[href*=\\#]:not([href=\\#])').click(function() {
    if (location.pathname.replace(/^\//,'') == this.pathname.replace(/^\//,'') && location.hostname == this.hostname) {
      var target = $(this.hash);
      target = target.length ? target : $('[name=' + this.hash.slice(1) +']');
      if (target.length) {
        $('html,body').animate({
          scrollTop: target.offset().top
        }, 1000);
        return false;
      }
    }
  });
})(jQuery);

// show sorting
(function($) {
	$('.genres a').click(function(e){
		var element = this.id;
		
		e.preventDefault();
		$(this).addClass("active").siblings().removeClass("active");

    	$(".theshows").find(".show").addClass('hide').fade;
    	$(".theshows").find("." + this.id).removeClass('hide');

	});
})(jQuery);

// mousewheel fix
(function($) {
    $("html").niceScroll();
})(jQuery);

// expand cast fix
(function($) {
    $(".cast-crew").on('click', '.expand-cast', function(event) {
    	event.preventDefault();
    	var text = $('.expand-cast span').text();
    	$('.expand-cast span').text(text == 'Show More' ? "Show Less" : "Show More");
    	$(this).toggleClass('rotate');
    	$('.cast, .crew').toggleClass('expand');
    	$('.grad').toggle();
    });
})(jQuery);
